	$('button[name="proceed"]').click(function(){
		var nickname = $('input[name="nickname"]').val();
		var error = false;
		if(platformID == 0){
			error = 'Select Platform';
		}
		if(nickname == ''){
			error = 'Type your nickname';
		}
		if(error){
			$('#errorModal .modal-text').html(error);
			$('#errorModal').modal('show');
		}else{
			var platform = platforms[platformID-1];
			$('.platform-name').html(platform + '<div class="ml-1 card-name">' + nickname + '</div>');
			$('.special-offer').fadeOut('slow',function(){
				$('.products-block').fadeIn('slow');
			});
		}
	});
	
